            using Daniel.Liu.DAO;

            //创建使用默认数据连接的SQL数据访问接口
            CommonInterface pComm=CommonFactory.CreateInstance(CommonData.sql,pConnectionString);

            //开始一个事务
            pComm.BeginTrans();

            string pSql="";
            DataSet pDst=new DataSet();

            //进行一些数据访问操作的代码
            pSql="select * from YourTable";

            //执行此次数据操作
            pDst=pComm.ExeForDst(pSql);

            //提交本次的事务
            pComm.CommitTrans();
            //关闭数据库连结
            pComm.Close();

